import React, { useState } from 'react';
import axios from 'axios';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Função para realizar o login
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Envia os dados para o backend
      const response = await axios.post('http://localhost:5000/login', { email, password });

      if (response.data.accessToken) {
        alert('Login bem-sucedido!');
        // Aqui você pode salvar o token no localStorage ou redirect para outra página
        localStorage.setItem('accessToken', response.data.accessToken);
        // Redirecionar, por exemplo, para a página inicial
        window.location.href = "/home"; // Substitua com o redirecionamento necessário
      } else {
        setError('Credenciais inválidas. Tente novamente.');
      }
    } catch (err) {
      console.error('Erro ao realizar login', err);
      setError('Erro ao realizar login. Verifique sua conexão ou tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: '0 auto' }}>
      <h2>Login com Microsoft</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
          />
        </div>
        <div>
          <label>Senha</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
          />
        </div>
        <button type="submit" style={{ width: '100%', padding: '10px' }} disabled={loading}>
          {loading ? 'Carregando...' : 'Login'}
        </button>
      </form>
      {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
    </div>
  );
};

export default LoginPage;
