import React, { useEffect, useState } from 'react';
import { PublicClientApplication } from "@azure/msal-browser";
import './App.css';

function App() {
    const [authConfig, setAuthConfig] = useState(null);

    useEffect(() => {
        // Consumir a API do backend para pegar as configurações de autenticação
        fetch('http://localhost:5000/auth-config')
            .then(response => response.json())
            .then(data => setAuthConfig(data))
            .catch(error => console.error("Erro ao obter configurações de autenticação:", error));
    }, []);

    if (!authConfig) {
        return <div>Carregando...</div>;
    }

    const msalConfig = {
        auth: {
            clientId: authConfig.clientId,
            authority: `https://login.microsoftonline.com/${authConfig.tenantId}`,
            redirectUri: authConfig.redirectUri
        }
    };

    const msalInstance = new PublicClientApplication(msalConfig);

    // Agora você pode usar msalInstance para autenticação no seu frontend

    return (
        <div>
            <h1>Login com Azure AD</h1>
            {/* Aqui você pode adicionar seu componente de login */}
        </div>
    );
}

export default App;
