import React, { useEffect, useState, createContext, useContext } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";
import { PublicClientApplication } from "@azure/msal-browser";
import { MsalProvider } from "@azure/msal-react";
import LoginPage from "./LoginPage";
import Dashboard from "./Dashboard";
import "./App.css";
import AdminPanel from "./AdminPanel";
import Home from "./Home";

const AuthContext = createContext(null);

export function useAuth() {
    return useContext(AuthContext);
}

const msalConfig = {
    auth: {
        clientId: "SEU_CLIENT_ID",
        authority: "https://login.microsoftonline.com/SEU_TENANT_ID",
        redirectUri: "http://localhost:3000",
    },
};

const msalInstance = new PublicClientApplication(msalConfig);

export function AuthProvider({ children }) {
    const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("accessToken"));

    useEffect(() => {
        const handleStorageChange = () => {
            setIsAuthenticated(!!localStorage.getItem("accessToken"));
        };

        window.addEventListener("storage", handleStorageChange);
        return () => {
            window.removeEventListener("storage", handleStorageChange);
        };
    }, []);

    return (
        <AuthContext.Provider value={{ isAuthenticated, setIsAuthenticated }}>
            {children}
        </AuthContext.Provider>
    );
}

function AuthenticatedApp() {
    const navigate = useNavigate();
    const { setIsAuthenticated } = useAuth();

    const handleLogin = async () => {
        try {
            const loginResponse = await msalInstance.loginPopup({ scopes: ["User.Read"] });
            localStorage.setItem("accessToken", loginResponse.accessToken);
            setIsAuthenticated(true);
            navigate("/Home");
        } catch (error) {
            console.error("Erro ao fazer login:", error);
        }
    };

    return (
        <div>
            <h1>Login com Azure AD</h1>
            <button onClick={handleLogin}>Entrar com Microsoft</button>
        </div>
    );
}

function App() {
    const { isAuthenticated } = useAuth();

    return (
        <Routes>
            <Route path="/" element={<Navigate to={isAuthenticated ? "/Home" : "/login"} />} />
            <Route path="/login" element={isAuthenticated ? <Navigate to="/Home" /> : <LoginPage />} />
            <Route path="/Home" element={isAuthenticated ? <Home /> : <Navigate to="/login" />} />
            <Route path="/dashboard" element={isAuthenticated ? <Dashboard /> : <Navigate to="/login" />} />
            <Route path="/AdminPanel" element={isAuthenticated ? <AdminPanel /> : <Navigate to="/login" />} />
        </Routes>
    );
}

export default function Root() {
    return (
        <MsalProvider instance={msalInstance}>
            <AuthProvider>
                <App />
            </AuthProvider>
        </MsalProvider>
    );
}
